mapper.select:
SELECT D.EMP_ID, E.EMP_NM, D.ATTENT_TM, D.LVFFC_TM, D.TOTAL_TM, D.WRKOTSD_YN, D.DCLZ_RSN
FROM   DCLZ D
INNER  JOIN EMP E ON E.EMP_ID = D.EMP_ID
WHERE  D.DEL_YN = 'N'
AND    E.EMP_ID = #{empId}
AND    D.DCLZ_YMD = #{dclzYmd}

mapper.update:
UPDATE DCLZ
SET    ATTENT_TM = #{attentTm},
	   LVFFC_TM = #{lvffcTm},
	   WRKOTSD_YN = #{wrkotsdYn},
	   DCLZ_RSN = #{dclzRsn},
	   LAST_UPDT_TM = #{lastUpdtTm},
	   LAST_UPDUSR_ID = #{lastUpdusrId}
WHERE  EMP_ID = #{empId}
AND    DCLZ_YMD = #{dclzYmd}

mapper.insert:
	INSERT INTO DCLZ
	    (DCLZ_NO, EMP_ID, FSRT_WRITING_TM, FRST_WRTER_ID, DCLZ_YMD, WRKOTSD_YN
	  , ATTENT_TM, LVFFC_TM, DCLZ_RSN, LAST_UPDT_TM, LAST_UPDUSR_ID, DEL_YN, TOTAL_TM)
	VALUES (
	    (SELECT NVL(MAX(DCLZ_NO), 0) + 1 FROM DCLZ)
	  , #{empId}
	  , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
	  , #{empId}
	  , TO_CHAR(SYSDATE, 'YYYYMMDD')
	  , 'N'
	  , #{attentTm}
	  , #{lvffcTm}
	  , #{dclzRsn}
	  , TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
	  , #{empId}
	  , 'N'
	  , #{totalTm}
   )

mapper.update:
UPDATE DCLZ
SET    LVFFC_TM = #{lvffcTm}
WHERE  EMP_ID = #{empId}
AND    DCLZ_YMD = TO_CHAR(SYSDATE, 'YYYYMMDD')
