mapper.insert:
INSERT INTO loan_book (
LON_NO
, MEM_ID
, BOOK_NO
, BEGIN_YMD
, END_YMD
, LOAN_STTS_CD
, LOAN_BOOK_NO
, RETURN_BOOK_NO
) VALUES (
(SELECT NVL((MAX(LON_NO)+1),1) FROM LOAN_BOOK)
, #{memId}
, #{bookNo}
, to_char(SYSDATE, 'YYYYMMDD')
, to_char(SYSDATE + 7, 'YYYYMMDD')
, 'LOAN01'
, #{loanBookNo}
, #{loanBookNo}
)

mapper.update:
UPDATE loan_book
SET
LOAN_STTS_CD = 'LOAN02'
WHERE MEM_ID = #{memId}
AND RETURN_BOOK_NO =
#{returnBookNo}
