mapper.insert:

	SELECT NVL((MAX(BOOK_NO)+1),1) FROM BOOK


INSERT INTO BOOK (
    BOOK_NO
    , BOOK_SE_CD
    , BOOK_SJ
    , AUTHR
    , INVNTRY
    , BOOK_IMG
) VALUES (
    #{bookNo}
    , #{bookSeCd}
    , #{bookSj}
    , #{authr}
    , #{invntry}
    , #{bookImg}
)

mapper.update:
UPDATE BOOK
    SET
        BOOK_SE_CD = #{bookSeCd}
        , BOOK_SJ = #{bookSj}
        , AUTHR = #{authr}
        , INVNTRY = #{invntry}
        , BOOK_IMG = #{bookImg}
WHERE
    book_no = #{bookNo}
